/// Tabulate the tensor for the contribution from a local cell
void bt_combsol_cell_integral_0_0::tabulate_tensor(double* A,
                                    const double * const * w,
                                    const ufc::cell& c) const
{
    // Extract vertex coordinates
    const double * const * x = c.coordinates;
    
    // Compute Jacobian of affine map from reference cell
    const double J_00 = x[1][0] - x[0][0];
    const double J_01 = x[2][0] - x[0][0];
    const double J_10 = x[1][1] - x[0][1];
    const double J_11 = x[2][1] - x[0][1];
    
    // Compute determinant of Jacobian
    double detJ = J_00*J_11 - J_01*J_10;
    
    // Compute inverse of Jacobian
    
    // Set scale factor
    const double det = std::abs(detJ);
    
    // Cell Volume.
    
    // Compute circumradius, assuming triangle is embedded in 2D.
    
    
    // Facet Area.
    
    // Array of quadrature weights.
    static const double W3[3] = {0.166666666666667, 0.166666666666667, 0.166666666666667};
    // Quadrature points on the UFC reference element: (0.166666666666667, 0.166666666666667), (0.166666666666667, 0.666666666666667), (0.666666666666667, 0.166666666666667)
    
    // Value of basis functions at quadrature points.
    static const double FE0[3][3] = \
    {{0.666666666666667, 0.166666666666667, 0.166666666666667},
    {0.166666666666667, 0.166666666666667, 0.666666666666667},
    {0.166666666666667, 0.666666666666667, 0.166666666666667}};
    
    // Array of non-zero columns
    static const unsigned int nzc1[3] = {3, 4, 5};
    
    // Array of non-zero columns
    static const unsigned int nzc0[3] = {0, 1, 2};
    
    // Array of non-zero columns
    static const unsigned int nzc3[3] = {9, 10, 11};
    
    // Array of non-zero columns
    static const unsigned int nzc2[3] = {6, 7, 8};
    
    // Reset values in the element tensor.
    for (unsigned int r = 0; r < 12; r++)
    {
      A[r] = 0.0;
    }// end loop over 'r'
    // Number of operations to compute geometry constants: 3.
    double G[2];
    G[0] = det*(1.0 - w[0][0]);
    G[1] = det*w[0][0];
    
    // Compute element tensor using UFL quadrature representation
    // Optimisations: ('eliminate zeros', True), ('ignore ones', True), ('ignore zero tables', True), ('optimisation', 'simplify_expressions'), ('remove zero terms', True)
    
    // Loop quadrature points for integral.
    // Number of operations to compute element tensor for following IP loop = 168
    for (unsigned int ip = 0; ip < 3; ip++)
    {
      
      // Coefficient declarations.
      double F0 = 0.0;
      double F1 = 0.0;
      double F2 = 0.0;
      double F3 = 0.0;
      
      // Total number of operations to compute function values = 24
      for (unsigned int r = 0; r < 3; r++)
      {
        F0 += FE0[ip][r]*w[3][r];
        F1 += FE0[ip][r]*w[4][r];
        F2 += FE0[ip][r]*w[1][r];
        F3 += FE0[ip][r]*w[2][r];
      }// end loop over 'r'
      
      // Number of operations to compute ip constants: 8
      double I[4];
      // Number of operations: 2
      I[0] = F2*G[0]*W3[ip];
      
      // Number of operations: 2
      I[1] = F3*G[0]*W3[ip];
      
      // Number of operations: 2
      I[2] = F0*G[1]*W3[ip];
      
      // Number of operations: 2
      I[3] = F1*G[1]*W3[ip];
      
      
      // Number of operations for primary indices: 24
      for (unsigned int j = 0; j < 3; j++)
      {
        // Number of operations to compute entry: 2
        A[nzc0[j]] += FE0[ip][j]*I[0];
        // Number of operations to compute entry: 2
        A[nzc1[j]] += FE0[ip][j]*I[1];
        // Number of operations to compute entry: 2
        A[nzc2[j]] += FE0[ip][j]*I[2];
        // Number of operations to compute entry: 2
        A[nzc3[j]] += FE0[ip][j]*I[3];
      }// end loop over 'j'
    }// end loop over 'ip'
}

